<?php$route = '/results_point_of_contact/:id/';$app->get($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['sort'])){ $sort = $_GET['sort']; } else { $sort = '';}	if(isset($_GET['page'])){ $page = $_GET['page']; } else { $page = 0;}	if(isset($_GET['count'])){ $count = $_GET['count']; } else { $count = 25;}	if(isset($_GET['in'])){ $in = $_GET['in']; } else { $in = '';}	$ReturnObject = array();	$Query = "SELECT * FROM results_point_of_contact WHERE id = '" . $id . "'";	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$ID = $Database['ID'];		$point_of_contact_id = $Database['point_of_contact_id'];		$nct_id = $Database['nct_id'];		$name_or_title = $Database['name_or_title'];		$organization = $Database['organization'];		$phone = $Database['phone'];		$email = $Database['email'];		$F = array();		$F['ID'] = $ID;		$F['point_of_contact_id'] = $point_of_contact_id;		$F['nct_id'] = $nct_id;		$F['name_or_title'] = $name_or_title;		$F['organization'] = $organization;		$F['phone'] = $phone;		$F['email'] = $email;		array_push($ReturnObject, $F);		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>