<?php$route = '/results_point_of_contact/:id/';$app->put($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['point_of_contact_id'])){ $point_of_contact_id = $_GET['point_of_contact_id']; } else { $point_of_contact_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['name_or_title'])){ $name_or_title = $_GET['name_or_title']; } else { $name_or_title = '';}	if(isset($_GET['organization'])){ $organization = $_GET['organization']; } else { $organization = '';}	if(isset($_GET['phone'])){ $phone = $_GET['phone']; } else { $phone = '';}	if(isset($_GET['email'])){ $email = $_GET['email']; } else { $email = '';}	$Query = "SELECT * FROM results_point_of_contact WHERE id = '" . $id . "'";	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$query = "UPDATE results_point_of_contact SET";		if(isset($id))			{			$query .= "id='" . mysql_real_escape_string($id) . "'";			}		if(isset($point_of_contact_id))			{			$query .= "point_of_contact_id='" . mysql_real_escape_string($point_of_contact_id) . "'";			}		if(isset($nct_id))			{			$query .= "nct_id='" . mysql_real_escape_string($nct_id) . "'";			}		if(isset($name_or_title))			{			$query .= "name_or_title='" . mysql_real_escape_string($name_or_title) . "'";			}		if(isset($organization))			{			$query .= "organization='" . mysql_real_escape_string($organization) . "'";			}		if(isset($phone))			{			$query .= "phone='" . mysql_real_escape_string($phone) . "'";			}		if(isset($email))			{			$query .= "email='" . mysql_real_escape_string($email) . "'";			}		$query .= " WHERE id = '" . $id . "'";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Results_point_of_contact Updated!";		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>