<?php$route = '/results_point_of_contact/';$app->post($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['point_of_contact_id'])){ $point_of_contact_id = $_GET['point_of_contact_id']; } else { $point_of_contact_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['name_or_title'])){ $name_or_title = $_GET['name_or_title']; } else { $name_or_title = '';}	if(isset($_GET['organization'])){ $organization = $_GET['organization']; } else { $organization = '';}	if(isset($_GET['phone'])){ $phone = $_GET['phone']; } else { $phone = '';}	if(isset($_GET['email'])){ $email = $_GET['email']; } else { $email = '';}		$query = "INSERT INTO results_point_of_contact(";		if(isset($point_of_contact_id)){ $query .= $point_of_contact_id . ","; }		if(isset($nct_id)){ $query .= $nct_id . ","; }		if(isset($name_or_title)){ $query .= $name_or_title . ","; }		if(isset($organization)){ $query .= $organization . ","; }		if(isset($phone)){ $query .= $phone . ","; }		if(isset($email)){ $query .= $email . ","; }		$query .= ") VALUES(";		if(isset($point_of_contact_id)){ $query .= "'" . mysql_real_escape_string($point_of_contact_id) . "',"; }		if(isset($nct_id)){ $query .= "'" . mysql_real_escape_string($nct_id) . "',"; }		if(isset($name_or_title)){ $query .= "'" . mysql_real_escape_string($name_or_title) . "',"; }		if(isset($organization)){ $query .= "'" . mysql_real_escape_string($organization) . "',"; }		if(isset($phone)){ $query .= "'" . mysql_real_escape_string($phone) . "',"; }		if(isset($email)){ $query .= "'" . mysql_real_escape_string($email) . "',"; }		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Results_point_of_contact Added!";	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>