<?php$route = '/results_restriction_agreement/:id/';$app->put($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['agreement_id'])){ $agreement_id = $_GET['agreement_id']; } else { $agreement_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['pi_employee'])){ $pi_employee = $_GET['pi_employee']; } else { $pi_employee = '';}	if(isset($_GET['restrictive_agreement'])){ $restrictive_agreement = $_GET['restrictive_agreement']; } else { $restrictive_agreement = '';}	$Query = "SELECT * FROM results_restriction_agreement WHERE id = '" . $id . "'";	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$query = "UPDATE results_restriction_agreement SET";		if(isset($id))			{			$query .= "id='" . mysql_real_escape_string($id) . "'";			}		if(isset($agreement_id))			{			$query .= "agreement_id='" . mysql_real_escape_string($agreement_id) . "'";			}		if(isset($nct_id))			{			$query .= "nct_id='" . mysql_real_escape_string($nct_id) . "'";			}		if(isset($pi_employee))			{			$query .= "pi_employee='" . mysql_real_escape_string($pi_employee) . "'";			}		if(isset($restrictive_agreement))			{			$query .= "restrictive_agreement='" . mysql_real_escape_string($restrictive_agreement) . "'";			}		$query .= " WHERE id = '" . $id . "'";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Results_restriction_agreement Updated!";		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>