<?php$route = '/results_restriction_agreement/';$app->post($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['agreement_id'])){ $agreement_id = $_GET['agreement_id']; } else { $agreement_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['pi_employee'])){ $pi_employee = $_GET['pi_employee']; } else { $pi_employee = '';}	if(isset($_GET['restrictive_agreement'])){ $restrictive_agreement = $_GET['restrictive_agreement']; } else { $restrictive_agreement = '';}		$query = "INSERT INTO results_restriction_agreement(";		if(isset($agreement_id)){ $query .= $agreement_id . ","; }		if(isset($nct_id)){ $query .= $nct_id . ","; }		if(isset($pi_employee)){ $query .= $pi_employee . ","; }		if(isset($restrictive_agreement)){ $query .= $restrictive_agreement . ","; }		$query .= ") VALUES(";		if(isset($agreement_id)){ $query .= "'" . mysql_real_escape_string($agreement_id) . "',"; }		if(isset($nct_id)){ $query .= "'" . mysql_real_escape_string($nct_id) . "',"; }		if(isset($pi_employee)){ $query .= "'" . mysql_real_escape_string($pi_employee) . "',"; }		if(isset($restrictive_agreement)){ $query .= "'" . mysql_real_escape_string($restrictive_agreement) . "',"; }		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Results_restriction_agreement Added!";	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>