<?php$route = '/secondary_ids/';$app->post($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['sec_id'])){ $sec_id = $_GET['sec_id']; } else { $sec_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['secondary_id'])){ $secondary_id = $_GET['secondary_id']; } else { $secondary_id = '';}		$query = "INSERT INTO secondary_ids(";		if(isset($sec_id)){ $query .= $sec_id . ","; }		if(isset($nct_id)){ $query .= $nct_id . ","; }		if(isset($secondary_id)){ $query .= $secondary_id . ","; }		$query .= ") VALUES(";		if(isset($sec_id)){ $query .= "'" . mysql_real_escape_string($sec_id) . "',"; }		if(isset($nct_id)){ $query .= "'" . mysql_real_escape_string($nct_id) . "',"; }		if(isset($secondary_id)){ $query .= "'" . mysql_real_escape_string($secondary_id) . "',"; }		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Secondary_ids Added!";	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>