<?php$route = '/sponsors/';$app->post($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['sponsor_id'])){ $sponsor_id = $_GET['sponsor_id']; } else { $sponsor_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['sponsor_type'])){ $sponsor_type = $_GET['sponsor_type']; } else { $sponsor_type = '';}	if(isset($_GET['agency'])){ $agency = $_GET['agency']; } else { $agency = '';}	if(isset($_GET['agency_class'])){ $agency_class = $_GET['agency_class']; } else { $agency_class = '';}		$query = "INSERT INTO sponsors(";		if(isset($sponsor_id)){ $query .= $sponsor_id . ","; }		if(isset($nct_id)){ $query .= $nct_id . ","; }		if(isset($sponsor_type)){ $query .= $sponsor_type . ","; }		if(isset($agency)){ $query .= $agency . ","; }		if(isset($agency_class)){ $query .= $agency_class . ","; }		$query .= ") VALUES(";		if(isset($sponsor_id)){ $query .= "'" . mysql_real_escape_string($sponsor_id) . "',"; }		if(isset($nct_id)){ $query .= "'" . mysql_real_escape_string($nct_id) . "',"; }		if(isset($sponsor_type)){ $query .= "'" . mysql_real_escape_string($sponsor_type) . "',"; }		if(isset($agency)){ $query .= "'" . mysql_real_escape_string($agency) . "',"; }		if(isset($agency_class)){ $query .= "'" . mysql_real_escape_string($agency_class) . "',"; }		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Sponsors Added!";	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>