<?php$route = '/study_outcome/:id/';$app->get($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['sort'])){ $sort = $_GET['sort']; } else { $sort = '';}	if(isset($_GET['page'])){ $page = $_GET['page']; } else { $page = 0;}	if(isset($_GET['count'])){ $count = $_GET['count']; } else { $count = 25;}	if(isset($_GET['in'])){ $in = $_GET['in']; } else { $in = '';}	$ReturnObject = array();	$Query = "SELECT * FROM study_outcome WHERE id = '" . $id . "'";	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$ID = $Database['ID'];		$outcome_id = $Database['outcome_id'];		$nct_id = $Database['nct_id'];		$study_outcomes_type = $Database['study_outcomes_type'];		$measure = $Database['measure'];		$safety_issue = $Database['safety_issue'];		$time_frame = $Database['time_frame'];		$description = $Database['description'];		$F = array();		$F['ID'] = $ID;		$F['outcome_id'] = $outcome_id;		$F['nct_id'] = $nct_id;		$F['study_outcomes_type'] = $study_outcomes_type;		$F['measure'] = $measure;		$F['safety_issue'] = $safety_issue;		$F['time_frame'] = $time_frame;		$F['description'] = $description;		array_push($ReturnObject, $F);		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>