<?php$route = '/study_outcome/';$app->post($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['outcome_id'])){ $outcome_id = $_GET['outcome_id']; } else { $outcome_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['study_outcomes_type'])){ $study_outcomes_type = $_GET['study_outcomes_type']; } else { $study_outcomes_type = '';}	if(isset($_GET['measure'])){ $measure = $_GET['measure']; } else { $measure = '';}	if(isset($_GET['safety_issue'])){ $safety_issue = $_GET['safety_issue']; } else { $safety_issue = '';}	if(isset($_GET['time_frame'])){ $time_frame = $_GET['time_frame']; } else { $time_frame = '';}	if(isset($_GET['description'])){ $description = $_GET['description']; } else { $description = '';}		$query = "INSERT INTO study_outcome(";		if(isset($outcome_id)){ $query .= $outcome_id . ","; }		if(isset($nct_id)){ $query .= $nct_id . ","; }		if(isset($study_outcomes_type)){ $query .= $study_outcomes_type . ","; }		if(isset($measure)){ $query .= $measure . ","; }		if(isset($safety_issue)){ $query .= $safety_issue . ","; }		if(isset($time_frame)){ $query .= $time_frame . ","; }		if(isset($description)){ $query .= $description . ","; }		$query .= ") VALUES(";		if(isset($outcome_id)){ $query .= "'" . mysql_real_escape_string($outcome_id) . "',"; }		if(isset($nct_id)){ $query .= "'" . mysql_real_escape_string($nct_id) . "',"; }		if(isset($study_outcomes_type)){ $query .= "'" . mysql_real_escape_string($study_outcomes_type) . "',"; }		if(isset($measure)){ $query .= "'" . mysql_real_escape_string($measure) . "',"; }		if(isset($safety_issue)){ $query .= "'" . mysql_real_escape_string($safety_issue) . "',"; }		if(isset($time_frame)){ $query .= "'" . mysql_real_escape_string($time_frame) . "',"; }		if(isset($description)){ $query .= "'" . mysql_real_escape_string($description) . "',"; }		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Study_outcome Added!";	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>